mapper.select:
  		SELECT BOARDNUM, BOARDTITLE, BOARDWRITER,BOARDDATE, BOARDTOTAL, BOARDCONTENTS
		FROM BOARD
		WHERE BOARDNUM=#{boardNum}

mapper.update:
  		UPDATE BOARD SET
		BOARDTITLE = #{boardTitle},BOARDWRITER=#{boardWriter},BOARDTOTAL=#{boardTotal}, BOARDCONTENTS=#{boardContents}
		WHERE BOARDNUM=#{boardNum};
